INSERT INTO Documents (DocumentID, MedlineID, DocumentTitle, PublicationType, DocumentAbstract, DocumentAuthor, DocumentSource) VALUES (396, 87052479, "Normal radiographic findings after foreign body aspiration. When the history counts.", "JOURNAL ARTICLE.", "Foreign body aspiration in children is frequently associated with unilateral emphysema or atelectasis on chest x-ray. Two cases are reported of tracheal or bilateral foreign bodies in which the original chest x-rays were read as normal, but the history was suggestive of the foreign body aspiration. Early bronchoscopy can prevent the long-term morbidity that results from unrecognized tracheobronchial foreign bodies.
